function stripFields(query) {
  if(!query) return;
  var fields = query.$fields;
  if(fields) delete query.$fields;
  return fields;
}

function stripOptions(query) {
  var options = {};
  if(!query) return options;
  // performance
  if(query.$limit) options.limit = parseInt(query.$limit);
  if(query.$skip) options.skip = parseInt(query.$skip);
  if(query.$sort || query.$orderby) options.sort = query.$sort || query.$orderby;
  delete query.$limit;
  delete query.$skip;
  delete query.$sort;
  return options;
}

var crypto = require('crypto');

// Modified RFC 4122 v4 UUID
exports.create = function (length) {
  length = length || 16;
  var hexDigits = "0123456789abcdef";
  var s = crypto.randomBytes(length).toString('hex').split(''); // convert string to array
  s.length = length; // trim to length if bigger

  s[length - 3] = hexDigits.substr((s[length - 3] & 0x3) | 0x8, 1);

  // return the uuid
  return s.join('');
};

