        if (e && e.code !== 'MODULE_NOT_FOUND') {
          console.error();
          console.error("Error loading module node_modules/" + file);
          console.error(e.stack || e);
          if(process.send) process.send({moduleError: e || true});
          process.exit(1);
        }
