        }
      }
    } else {
      // read local project resources
      fs.readdir(path + '/node_modules', function(err, dir) {
        remaining = 0;
        if(dir && dir.length) {
          dir.forEach(function(file) {
            if(file.indexOf('.js') == file.length - 3 || file.indexOf('.') === -1) {
              loadCustomResources(file);
            }
          });
        } else {
          fn(defaults, types);
        }
      });
    }
  });
};

